import React, { Component } from 'react';
import PokemonImage from '../pokemon_image/PokemonImage.jsx';

import './PokemonDetails.scss';

class PokemonDetails extends Component {

  constructor(props, context) {
    super(props, context);
    this.state = {
      isLoading: true
    }
    this.escFunction = this.escFunction.bind(this);
    this.closePokemonDetails = this.closePokemonDetails.bind(this);
  }

  componentDidMount() {
    document.addEventListener("keydown", this.escFunction, false);

    fetch(this.props.pokemonUrl)
      .then(response => response.json())
      .then(data => {
        this.setState({ pokemon: data });
        this.setState({ isLoading: false });
      });
  }

  componentWillUnmount(){
    document.removeEventListener("keydown", this.escFunction, false);
  }

  escFunction(event) {
    if(event.keyCode === 27) {
      this.closePokemonDetails();
    }
  }

  closePokemonDetails() {
    this.props.closePokemonDetails();
  }

  render() {
    return (
      <div className="PokemonDetails">
        <div className="PokemonDetails-overlay" onClick={this.closePokemonDetails} />
        <div className="PokemonDetails-content">
          <div className="PokemonDetails-content-top">
            <div className="PokemonDetails-close">
              <img className="PokemonDetails-close-button" onClick={this.closePokemonDetails} src="data:image/svg+xml;utf8;base64,"/>
            </div>
          </div>
          { this.state.isLoading ? <div className="Loading"/> : null }
          <div className="PokemonDetails-content-pokemon">
            { this.state && this.state.pokemon ? <PokemonImage pokemon={this.state.pokemon}/> : null }
          </div>
        </div>
      </div>
    );
  }
}

export default PokemonDetails;
